class Poly:

    def __init__(self,coeffs):
        self.co=coeffs

    def __str__(self):
        s=str(self.co[0])
        for i,j in enumerate(self.co[1:],1):
            if j !=0:
                if j>0:
                    s+="+"
                s+=str(j)+"x^"+str(i)
        return s
    def polyder(self):
        coe = []
        for i in range(1, len(self.co)):
            coe.append(self.co[i]*i)
        return Poly(coe)

import math
class Circle:
   def __init__(self, center=[0,0], radius=1):
      self.center = center
      self.radius = radius 
   def __str__(self):
      return str(self.center) + " , " + str(self.radius)   
class Point:
    def __init__(self, x=0, y=0):
        self.x = x
        self.y = y

class Line:

    def __init__(self,slope,intercept):
        self.slope = slope
        self.intercept = intercept


    def __str__(self):
        return "y=" + str(self.slope) +"x+"+str(self.intercept)

    def intersectCircle(self,circle1):
      x0, y0 = circle1.center
      distance = abs(self.slope *x0-y0 + self.intercept)/ math.sqrt(self.slope ** 2 + 1)
      return distance <= circle1.radius

    def pointOnLine(self,point):
       return point.y == point.x * self.slope + self.intercept

